<?php/** * Шаблон made fermerjeck * https://github.com/fermerjeck * @package WordPress * @subpackage fermerjeck */?><div class="pitomnikbody clearfix ">    <?php    $args = array(        'parent' => 7,        'hide_empty' => 0,        'exclude' => '', // ID рубрики, которую нужно исключить        'number' => '0',        'taxonomy' => 'category', // таксономия, для которой нужны изображения        'pad_counts' => true    );    $catlist = get_categories($args);    ?>    <div class="pitomnikbox">        <ul>            <?php foreach ($catlist as $cat) : ?>                <li class="pitli">                    <a href="<?php echo get_term_link($cat->slug, 'category'); ?>">                    <img src="<?php echo z_taxonomy_image_url($cat->term_id, [150,210]); ?>"/>                    <div class="pitlink">                        <?php echo $cat->name; ?>                    </div>                    </a>                </li>            <?php endforeach; ?>        </ul>    </div>    <div class="leika"></div></div><?php wp_reset_postdata(); ?>