<?php/** * Шаблон made fermerjeck * https://github.com/fermerjeck * @package WordPress * @subpackage fermerjeck */?><div class="pitomnikbody clearfix ">    <?php $query = new WP_Query( 'category__in=9' ); ?>    <div class="pitomnikbox">        <ul>            <?php while ( $query->have_posts()) : $query->the_post(); ?>                <?php                $gallery = get_post_gallery( get_the_ID(), false );                $gids = explode( ",", $gallery['ids'] );                foreach ($gids as $id) {                    echo " <li>";                $attachment = wp_prepare_attachment_for_js($id);                ?>                    <a class="popup-pitomnik" href="#<?php echo $id;?>"><img src="<?php echo $attachment['url']; ?>" class="my-custom-class" alt="<?php echo $attachment['caption']; ?>" />                <?php                    echo  "<span>".$attachment['caption']."</span></a>";                    echo "<div id='$id' class='inboxpit'>";                    echo "<br>";                    echo  "<h3>".$attachment['caption']."</h3><br>".$attachment['description'] ;                    echo "<p><a class=\"popup-modal-dismiss\" href=\"#\">Закрыть</a></p> </div>";                    echo " </li>";            }                ?>            <?php endwhile; ?>            <?php wp_reset_postdata();?>        </ul>    </div>    <div class="leika"></div></div><?php wp_reset_postdata(); ?>