<?php/** * Шаблон made fermerjeck * https://github.com/fermerjeck * @package WordPress * @subpackage fermerjeck */?><div class="gusibody ">    <?php    $args=array(        'orderby' => 'name',        'child_of' => 7,        'order' => 'ASC'    );    $categories=get_categories($args);    $cat = [];    foreach($categories as $category) {        $cat[] =  $category->cat_ID ;    }    $args = array(        'category__in' => $cat,        'posts_per_page' => 10,    );    $query = new WP_Query($args); ?>    <header><a href="<?php echo get_category_link(7); ?>">Питомник растений</a></header>    <div class="pitomnikbody clearfix">    <?php    $i = 0;    $className = '';    while ( $query->have_posts()) : $query->the_post();        $i++;        ?>            <?php        if($i == 1 )$className = "pitomnikbg1";        elseif ($i== 2)$className = "pitomnikbg2";        elseif ($i== 3)$className = "pitomnikbg3";        ?>            <div class="pitomnikbox">                <div class="<?=$className?> pitbg"></div>            <div class="pitomnikimg">                <a href="<?php the_permalink(); ?>"><?php the_post_thumbnail(array(800,600));?></a>            </div>         <div class="pitomniktext">                <h2><a href="<?php the_permalink()?>"><?php the_title(); ?></a> </h2>            </div>            </div>    <?php endwhile; ?>       <?php wp_reset_query();?>        </div>    <h4><a href="<?php echo get_category_link(7); ?>">Посмотреть всё</a></h4>    </div><?php wp_reset_postdata();?>